import React, {useState, useEffect} from "react"
import {useParams} from "react-router-dom" // ver para que es el Use params
import styles from "./Detail.module.css"

export default function Detail(props){
    const {detailId} = useParams()
    const [character, setCharacter] = useState({}) 
    useEffect(()=>{
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
        .then((res)=> res.json())
        .then((data)=> {data.name ? setCharacter(data): alert("No hay personajes con ese nombre")})
        .catch((err)=> {console.log(err)
        alert("algo salio mal")})
    }, [])
    return(
        <div className={styles.container    }>
            <h1>Name: {character.name}</h1>
            <h1>Status: {character.status}</h1>
            <h1>Specie: {character.specie}</h1>
            <h1>Gender: {character.gender}</h1>
            <h1>Origin: {character.origin}</h1>
        </div>
    )
}
