import { redirect } from "react-router-dom";
import styles from "./Form.module.css"
import { useEffect, useState } from "react";

export default function From(props){
   
    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
    const validLettersAndNumbers = new RegExp(/^(?=.*[a-z])(?=.*[0-9])/);

    const validate =(inputs) =>{
        //inputs
        const errors = {}
        if(!regexEmail.test(inputs.userName)){/*si no es un formato de email*/
            errors.userName= "Debe ser un email";
        }
        if(!inputs.userName){
            errors.userName ="No puede ser vacio";
        }
        if(inputs.userName.length > 35){
            errors.userName ="no pueden haber mas de 35 caracteres";
        }
        if(!validLettersAndNumbers.test(inputs.password)){
            errors.password = "Debe contener almenos un numero";
        }
        if(inputs.password.length < 6 || inputs.password.length > 10 ){
            errors.password = "Debe tener de 6 a 10 caracteres";
        }
        return errors; // se retorna el objto estado "error" con la propiedad name segun el if al que entre
    }
    
    const [userData, setUserData ] = useState({ userName: "", password:"" });
    const [errors, setErrors] = useState({userName:"", password:""});

    const handleChange =(event) =>{
        const {name, value} = event.target;
        setUserData({...userData, [name]: value});// con el ...userData se hace una copia del userData original de la linea 30
        setErrors(validate({...userData, [name]:value}))// llega el return de la linea 27
    };

    

    useEffect(()=> {}, [userData])

    return( <form className={styles.container}>

            <label htmlFor ="">Nombre: </label>
            <input type="text" value={userData.userName} name="userName" onChange={handleChange}
            className={errors.userName && "warning"}/>
            {errors.userName ? <p style={{color:"red"}}>{errors.userName}</p>: null}
            <label htmlFor="">Password: </label>
            {errors.password ? <p style={{color:"red"}}>{errors.password}</p>: null}
            <input type="password" value={userData.password} name="password" onChange={handleChange}
            className={errors.password && "warning"}/>
            <button>Login</button>

    </form>
    );
}
