import './App.css'
import { useState } from 'react'
import Card from './components/Card/Card.jsx'
import Cards from './components/Cards/Cards.jsx'
import SearchBar from './components/searchBar/SearchBar.jsx'
import characters, { Rick } from './data.js'
import Nav from "./components/nav/Nav";
import {Routes, Route, useLocation}  from "react-router-dom"
import About from "./components/about/About";
import Detail from "./components/detail/detail" // por qué no puedo ponerle D mayuscula al archivo detail.jsx
import Form from "./components/form/Form";

function App () {
  const location = useLocation() 
  const [characters, setCharacters] = useState([])
 /* const example = {
    name: 'Morty Smith',
    species: 'Human',
    gender: 'Male',
    image: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg',
 };*/
 const onSearch = (id) =>{
    fetch(`https://rickandmortyapi.com/api/character/${id}`)
    .then((res)=>res.json())
    .then((data)=>{(data.name ? characters.filter((char)=>             // SUPER 
      char.id === data.id).length === 0: "")                            // IMPORTANTE: (debuggear para ver como funciona)
      ? setCharacters([...characters, data]):alert("Pesonaje ya existe") // PARA VERIFICAR SI BUSCA AGREGAR 
    })                                                                  // UN PERSONAJE QUE YA FUE AGREGADO EN EL HOME
    .catch((error)=>console.log(error)) 
 }

 const onClose = (id) =>{
  const filtered = characters.filter((char)=> char.id !==   id)
  setCharacters(filtered)
 }

  return (
    <div className='App' style={{ padding: '25px' }}>
     {location.pathname !== "/" &&
     <Nav onSearch={onSearch}/>}
      <hr/>
      <div style={{display:"flex", justifyContent:"center"}}>
        <Card
          id = {Rick.id}
          name={Rick.name}
          species={Rick.species}
          gender={Rick.gender}
          image={Rick.image}
          onClose={() => window.alert('Emulamos que se cierra la card')}
        />
      </div>
      <hr />
      <div>
        <Routes>
          <Route path="/" element={<Form/>}/>
          <Route path="/home" element={<Cards characters={characters} onClose={onClose} />}/>
          <Route path="/about" element={<About />}/>
          <Route path="/detail/:detailId" element={<Detail/>}/>
        </Routes>

      </div>
      <hr />

    </div>
  )
}

export default App
